function AnchorPointInit(){$(".anchor_directory").each((function(){var t=$(this);t.children("ul").empty(),$(".anchor_title").each((function(){var n=$(this),e=n.text().indexOf("\n")>-1?n.text().substr(0,n.text().indexOf("\n")):n.text();t.children("ul").append(`<li class="fs-5"><a class="text-black text-decoration-none" href="#${n.attr("id")}"><div class="p-2 px-4">${e}</div></a></li>`),n.bind("DOMNodeInserted",(function(){AnchorPointInit()}))})),t.children('a[href^="#"]').click((function(t){var n=$(this).attr("href");console.log(n);var e=$(n).offset().top-50;$("html, body").animate({scrollTop:e},0),t.preventDefault()}))}))}