function PageReady(){const t=$("#ContactForm");Array.from(t).forEach((t=>{t.addEventListener("submit",(e=>{t.checkValidity()?(e.preventDefault(),Coker.sweet.success("成功送出表單！",null,!0),setTimeout((function(){$("#ContactForm").submit()}),1500)):(e.preventDefault(),e.stopPropagation()),t.classList.add("was-validated")}),!1)}))}